mapper.select:
SELECT M_TITLE
FROM M_REPLY
WHERE M_TITLE =#{m_title}
AND M_DIRECTOR =#{m_director}
AND M_ID =#{m_id}

mapper.update:
UPDATE M_REPLY 
SET M_RATE =#{m_rate}
WHERE M_TITLE =#{m_title}
AND M_DIRECTOR =#{m_director}
AND M_ID =#{m_id}

mapper.select:
SELECT M_RATE
FROM M_REPLY
WHERE M_TITLE =#{m_title}
AND M_DIRECTOR =#{m_director}
AND M_ID =#{m_id}

mapper.update:
UPDATE M_REPLY
SET M_COMMENT=#{m_comment}
WHERE M_TITLE =#{m_title}
AND M_DIRECTOR =#{m_director}
AND M_ID =#{m_id}

mapper.select:
SELECT M_ID ,M_COMMENT ,M_RATE 
FROM M_REPLY
WHERE M_TITLE =#{m_title}
AND M_DIRECTOR =#{m_director}
ORDER BY M_SEQ
